  `check_info` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'These values are per-check, and can''t be compared across checks.'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

